package mypack;

public class ComplementOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 5;
        int num2 = 3;

        System.out.println("AND: " + (num1 & num2));
        System.out.println("OR: " + (num1 | num2));
        System.out.println("XOR: " + (num1 ^ num2));
        System.out.println("Complement of num1: " + (num1));
        System.out.println("Complement of num2: " + (num2));
	}

}
